-- ----------------------------
-- Table structure for lc_law_article
-- ----------------------------
DROP TABLE IF EXISTS `lc_law_article`;
CREATE TABLE `lc_law_article` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `order_value` int(11) DEFAULT NULL,
  `title` varchar(500) DEFAULT NULL,
  `content` longtext,
  `created_by` bigint(20) DEFAULT NULL,
  `created_time` datetime DEFAULT NULL,
  `lastmodified_by` bigint(20) DEFAULT NULL,
  `lastmodified_time` datetime DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of lc_law_article
-- ----------------------------
